import React, { useContext } from "react";

import { ReactComponent as ShoppingIcon } from "../../assets/shopping-bag.svg";
import { CartContext } from "../../context/cart.context";
import "./cart-icon.styles.scss";

const CartIcon = () => {
    const { isCartOpen, cartItems, setIsCartOpen } = useContext(CartContext)

    const toggleIsCartOpen = () => setIsCartOpen(!isCartOpen);

    const addedToCart = cartItems.reduce((accumulator, currentElement) => 
        accumulator + currentElement.quantity)

    return(
        <div className="cart-icon-container" onClick={toggleIsCartOpen}>
            <ShoppingIcon className="shopping-icon"/>
            <span className="item-count" >{addedToCart}</span>
        </div>
    )
}

export default CartIcon;
