import { onAuthStateChangedListener } from "../utils/firebase/firebase.utils"

// the actual value we want to access 
// pass/create the default value
export const UserContext = createContext({
    currentUser: null,
    setCurrentUser: () => null,
});


// provider is the actual component
// the provider allows any of its child components to access the state inside of its useState
export const UserProvider = ({children}) => {
    const [currentUser, setCurrentUser] = useState(null);
    const value = { currentUser, setCurrentUser };

    useEffect(()=> {    // 
        onAuthStateChangedListener(() => {  // receives callback function and passes this callback function as second parameter to onauthstagechange in firebase utils

        })
    }, [])

    return (
        <UserContext.Provider value={value}> 
            {children}
        </UserContext.Provider>
    )
};
